INSERT INTO `journal_contents` (`id`, `journal_id`, `locale`, `title`, `content`) VALUES(275, 116, 'fi', 'Keyboard shortcut when editing scenario', '<p style="line-height: 18.5714px;">This article describes keyboard shortcuts that are available in SketchupOulu when editing or creating a scenario.</p><p style="line-height: 18.5714px;"><b>Note:</b></p><p style="line-height: 18.5714px;">- To use keyboard shortcut, the mouse must hover over the scenario''s area or the scenario must be in full screen mode.</p><p style="line-height: 18.5714px;">-&nbsp;For keyboard shortcuts in which you press two or more keys at the same time, the keys to press are separated by a plus sign (+).</p><h2 style="font-family: Helvetica, arial, nimbussansl, liberationsans, freesans, clean, sans-serif, ''Apple Color Emoji'', ''Segoe UI Emoji'', ''Segoe UI Symbol''; color: rgb(0, 0, 0);">Keyboard shortcuts to control scenario</h2><table class="table table-bordered" style="width: 1055px; line-height: 18.5714px;"><tbody><tr><td style="line-height: 1.42857; width: 738px;">Save scenario</td><td style="line-height: 1.42857; width: 316px;">Ctrl + S</td></tr><tr><td style="line-height: 1.42857;">Full screen mode</td><td style="line-height: 1.42857;">Ctrl + F</td></tr><tr><td style="line-height: 1.42857;"><span style="line-height: 18.5714px;">Enlarge scenario</span><br></td><td style="line-height: 1.42857;"><span style="line-height: 18.5714px;">Ctrl + Shift + =</span><br></td></tr><tr><td style="line-height: 1.42857;">Shrink scenario</td><td style="line-height: 1.42857;">Ctrl + Shift + -</td></tr><tr><td style="line-height: 1.42857;">Undo action</td><td style="line-height: 1.42857;">Ctrl + Z</td></tr><tr><td style="line-height: 1.42857;">Redo action</td><td style="line-height: 1.42857;">Ctrl + Y</td></tr></tbody></table>');
INSERT INTO `journal_contents` (`id`, `journal_id`, `locale`, `title`, `content`) VALUES(276, 116, 'en', 'Keyboard shortcut when editing scenario', '<p>This article describes keyboard shortcuts that are available in SketchupOulu when editing or creating a scenario.</p><p><b>Note:</b></p><p>- To use keyboard shortcut, the mouse must hover over the scenario''s area or the scenario must be in full screen mode.</p><p>-&nbsp;For keyboard shortcuts in which you press two or more keys at the same time, the keys to press are separated by a plus sign (+).</p><h2>Keyboard shortcuts to control scenario</h2><table class="table table-bordered"><tbody><tr><td style="width:70%">Save scenario</td><td style="width:30%">Ctrl + S</td></tr><tr><td>Full screen mode</td><td>Ctrl + F</td></tr><tr><td><span style="line-height: 18.5714px;">Enlarge scenario</span><br></td><td><span style="line-height: 18.5714px;">Ctrl + Shift + =</span><br></td></tr><tr><td>Shrink scenario</td><td>Ctrl + Shift + -</td></tr><tr><td>Undo action</td><td>Ctrl + Z</td></tr><tr><td>Redo action</td><td>Ctrl + Y</td></tr></tbody></table>');
INSERT INTO `journal_contents` (`id`, `journal_id`, `locale`, `title`, `content`) VALUES(277, NULL, 'fi', 'How to upload models', '<p style="margin: 0in 0in 7.5pt; line-height: 13.95pt;"><span style="font-size: 10pt; font-family: Helvetica, sans-serif;">At this moment SketchupOulu support the following file formats:<o:p></o:p></span></p><p style="margin: 0in 0in 7.5pt 0.5in; text-indent: -0.25in; line-height: 13.95pt;"><span style="font-size: 10pt; font-family: Symbol;">·<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><b><span style="font-size: 10pt; font-family: Helvetica, sans-serif;"><a href="https://en.wikipedia.org/wiki/COLLADA" target="_blank">COLLADA</a></span></b><span class="apple-converted-space"><span style="font-size: 10pt; font-family: Helvetica, sans-serif;">&nbsp;</span></span><span style="font-size: 10pt; font-family: Helvetica, sans-serif;">(COLLAborative Design Activity, normally with extension<span class="apple-converted-space">&nbsp;</span><b>.DAE</b>) is an interchange file format for interactive 3D applications. It is managed by the nonprofit technology consortium, the Khronos Group, and has been adopted by ISO as a publicly available specification, ISO/PAS 17506.<o:p></o:p></span></p><p style="margin: 0in 0in 7.5pt 0.5in; text-indent: -0.25in; line-height: 13.95pt;"><span style="font-size: 10pt; font-family: Symbol;">·<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 10pt; font-family: Helvetica, sans-serif;"><a href="https://en.wikipedia.org/wiki/Wavefront_.obj_file" target="_blank" style="color: rgb(35, 82, 124); text-decoration: underline; outline: 0px;"><b>Wavefront .obj file</b></a><span class="apple-converted-space">&nbsp;</span>(or<span class="apple-converted-space">&nbsp;</span><b>.OBJ</b>) is a geometry definition file format first developed by Wavefront Technologies for its Advanced Visualizer animation package. The file format is open and has been adopted by other 3D graphics application vendors. For the most part it is a universally accepted format.<o:p></o:p></span></p><p style="margin: 0in 0in 7.5pt 0.5in; text-indent: -0.25in; line-height: 13.95pt;"><span style="font-size: 10pt; font-family: Symbol;">·<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style="font-size: 10pt; font-family: Helvetica, sans-serif;">Image file<span class="apple-converted-space">&nbsp;</span><b>.PNG</b>,<span class="apple-converted-space">&nbsp;</span><b>.JPG</b><span class="apple-converted-space">&nbsp;</span>and<span class="apple-converted-space">&nbsp;</span><b>.JPEG</b><o:p></o:p></span></p><p style="margin: 0in 0in 7.5pt; line-height: 13.95pt;"><span style="font-size: 10pt; font-family: Helvetica, sans-serif;">This tutorial will guide you during the process of uploading your Sketchup model to SketchupOulu.&nbsp;</span></p><p style="margin: 0in 0in 7.5pt; line-height: 13.95pt;"><span style="line-height: 13.95pt; text-indent: -0.25in; font-size: 10pt; font-family: Helvetica, sans-serif;">1. First, export your model using Sketchup</span><span style="line-height: 13.95pt; text-indent: -0.25in;">. You can do that by clicking top menu File&nbsp;</span><b style="line-height: 13.95pt; text-indent: -0.25in;">(1)</b><span style="line-height: 13.95pt; text-indent: -0.25in;">&nbsp;=&gt; Export&nbsp;</span><b style="line-height: 13.95pt; text-indent: -0.25in;">(2)&nbsp;</b><span style="line-height: 13.95pt; text-indent: -0.25in;">=&gt; 3D Model&nbsp;</span><b style="line-height: 13.95pt; text-indent: -0.25in;">(3)</b></p><p style="margin: 0in 0in 7.5pt; line-height: 13.95pt;"><img src="http://vm0106.virtues.fi/static/images/email_pictures/YZZWBZH921BDfpJSJIiigj0NgDQ9AnoIVvzuBB630sVIvP4gUw.png" style="width: 383px;"></p><p style="margin: 0in 0in 7.5pt; line-height: 18.5714px;"><span style="line-height: 18.6px;">2. A window appears, choose “Save as type” as “OBJ file (.obj)”&nbsp;<b>(1)</b>, choose filename&nbsp;<b>(2)</b>&nbsp;and directory&nbsp;<b>(3)</b>&nbsp;then click Export button&nbsp;<b>(4)</b>. Assume filename is skyscraper.</span></p><p style="margin: 0in 0in 7.5pt; line-height: 18.5714px;"><img src="http://vm0106.virtues.fi/static/images/email_pictures/nGnAHiVybfCJQImkmkETqaRw0Qs876VfabVEQHF0UUxrKfbaFe.png" style="width: 474px;"></p><p style="margin: 0in 0in 7.5pt; line-height: 18.5714px;">3. After the exporting finishes, go to the exported directory. There you can see 2 files (skyscraper.obj, skyscraper.mtl) and a directory (skyscraper). Compress them all to a single zip file.</p><p style="margin: 0in 0in 7.5pt; line-height: 18.5714px;">4. Go to&nbsp;<a href="http://vm0106.virtues.fi/users/add_building_model/">http://vm0106.virtues.fi/users/add_building_model/</a>&nbsp;to upload the file.<br></p><p class="MsoNormal" style="line-height: 18.5714px;"><b>Note:</b><o:p></o:p></p><p class="MsoListParagraphCxSpFirst" style="line-height: 18.5714px; text-indent: -0.25in;">-<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span>- Use the same process with Collada file<o:p></o:p></p><p style="line-height: 18.5714px;"></p><p class="MsoListParagraphCxSpLast" style="line-height: 18.5714px; text-indent: -0.25in;">-<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>- With image files, you don’t need to compress them</p>');
INSERT INTO `journal_contents` (`id`, `journal_id`, `locale`, `title`, `content`) VALUES(278, NULL, 'en', 'How to upload models', '<p style="margin-top:0in;margin-right:0in;margin-bottom:7.5pt;margin-left:0in;\r\nline-height:13.95pt"><span style="font-size: 10pt; font-family: Helvetica, sans-serif;">At this moment SketchupOulu support the following file formats:<o:p></o:p></span></p><p style="margin: 0in 0in 7.5pt 0.5in; text-indent: -0.25in; line-height: 13.95pt;"><!--[if !supportLists]--><span style="font-size: 10pt; font-family: Symbol;">·<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span><!--[endif]--><b><span style="font-size: 10pt; font-family: Helvetica, sans-serif;"><a href="https://en.wikipedia.org/wiki/COLLADA" target="_blank">COLLADA</a></span></b><span class="apple-converted-space"><span style="font-size: 10pt; font-family: Helvetica, sans-serif;">&nbsp;</span></span><span style="font-size: 10pt; font-family: Helvetica, sans-serif;">(COLLAborative Design Activity, normally\r\nwith extension<span class="apple-converted-space">&nbsp;</span><b>.DAE</b>) is an interchange file format for\r\ninteractive 3D applications. It is managed by the nonprofit technology\r\nconsortium, the Khronos Group, and has been adopted by ISO as a publicly\r\navailable specification, ISO/PAS 17506.<o:p></o:p></span></p><p style="margin: 0in 0in 7.5pt 0.5in; text-indent: -0.25in; line-height: 13.95pt;"><!--[if !supportLists]--><span style="font-size: 10pt; font-family: Symbol;">·<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span><!--[endif]--><span style="font-size: 10pt; font-family: Helvetica, sans-serif;"><a href="https://en.wikipedia.org/wiki/Wavefront_.obj_file" target="_blank"><b>Wavefront .obj file</b></a><span class="apple-converted-space">&nbsp;</span>(or<span class="apple-converted-space">&nbsp;</span><b>.OBJ</b>) is a geometry definition file format\r\nfirst developed by Wavefront Technologies for its Advanced Visualizer animation\r\npackage. The file format is open and has been adopted by other 3D graphics\r\napplication vendors. For the most part it is a universally accepted format.<o:p></o:p></span></p><p style="margin: 0in 0in 7.5pt 0.5in; text-indent: -0.25in; line-height: 13.95pt;"><!--[if !supportLists]--><span style="font-size: 10pt; font-family: Symbol;">·<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span><!--[endif]--><span style="font-size: 10pt; font-family: Helvetica, sans-serif;">Image file<span class="apple-converted-space">&nbsp;</span><b>.PNG</b>,<span class="apple-converted-space">&nbsp;</span><b>.JPG</b><span class="apple-converted-space">&nbsp;</span>and<span class="apple-converted-space">&nbsp;</span><b>.JPEG</b><o:p></o:p></span></p><p style="margin: 0in 0in 7.5pt; line-height: 13.95pt;"><span style="font-size: 10pt; font-family: Helvetica, sans-serif;">This\r\ntutorial will guide you during the process of uploading your Sketchup model to\r\nSketchupOulu.&nbsp;</span></p><p style="margin: 0in 0in 7.5pt; line-height: 13.95pt;"><span style="line-height: 13.95pt; text-indent: -0.25in; font-size: 10pt; font-family: Helvetica, sans-serif;">1. First, export your model using Sketchup</span><span style="line-height: 13.95pt; text-indent: -0.25in;">.\r\nYou can do that by clicking top menu File </span><b style="line-height: 13.95pt; text-indent: -0.25in;">(1)</b><span style="line-height: 13.95pt; text-indent: -0.25in;">\r\n=&gt; Export </span><b style="line-height: 13.95pt; text-indent: -0.25in;">(2) </b><span style="line-height: 13.95pt; text-indent: -0.25in;">=&gt; 3D Model </span><b style="line-height: 13.95pt; text-indent: -0.25in;">(3)</b></p><p style="margin: 0in 0in 7.5pt; line-height: 13.95pt;"><img src="http://vm0106.virtues.fi/static/images/email_pictures/YZZWBZH921BDfpJSJIiigj0NgDQ9AnoIVvzuBB630sVIvP4gUw.png" style="width: 383px;"></p><p style="margin: 0in 0in 7.5pt;"><span style="line-height: 18.6px;">2. A window appears, choose “Save as type” as “OBJ file (.obj)” <b>(1)</b>, choose filename <b>(2)</b> and directory <b>(3)</b> then click Export button <b>(4)</b>. Assume filename is skyscraper.</span></p><p style="margin: 0in 0in 7.5pt;"><img src="http://vm0106.virtues.fi/static/images/email_pictures/nGnAHiVybfCJQImkmkETqaRw0Qs876VfabVEQHF0UUxrKfbaFe.png" style="width: 474px;"></p><p style="margin: 0in 0in 7.5pt;">3. After the exporting finishes, go to the exported directory. There you can see 2 files (skyscraper.obj, skyscraper.mtl) and a directory (skyscraper). Compress them all to a single zip file.</p><p style="margin: 0in 0in 7.5pt;">4. Go to <a href="http://vm0106.virtues.fi/users/add_building_model/">http://vm0106.virtues.fi/users/add_building_model/</a> to upload the file.<br></p><p class="MsoNormal"><b>Note: </b><o:p></o:p></p><p class="MsoListParagraphCxSpFirst" style="text-indent:-.25in;mso-list:l1 level1 lfo3"><!--[if !supportLists]-->-<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; </span>- Use the same process with Collada file<o:p></o:p></p><p>\r\n</p><p class="MsoListParagraphCxSpLast" style="text-indent:-.25in;mso-list:l1 level1 lfo3"><!--[if !supportLists]-->-<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span>- With image files, you don’t need to compress\r\nthem<o:p></o:p></p><p></p>');
INSERT INTO `journal_contents` (`id`, `journal_id`, `locale`, `title`, `content`) VALUES(279, 114, 'fi', 'About us', '<p style="line-height: 18.5714px;">SketchupOulu is the product of a joint project between Department of Computer Sciences, University of Oulu and Urban Planning division, Oulu city. The aim of the website is to create a collaborative environment where motivated citizens can contribute to the development of Oulu city. Using the website, not only you can communicate directly with official city planners, you can also design the city yourself and then send the blueprint for reviewing.</p><p style="line-height: 18.5714px;">The project is in active development in term of functionality and content richness. Main contributors:</p><p style="line-height: 18.5714px;"><b>From University of Oulu:</b></p><p style="line-height: 18.5714px;"></p><div style="line-height: 18.5714px; float: left;"><img src="data:image/jpeg;base64," data-filename="yo.jpg" style="width: 79.2857px; height: 111px;"></div><div style="line-height: 18.5714px; float: left; margin-left: 5px;">Thang Luu (Mr), development project manager<br>Pingjang Li<span style="line-height: 18.5714px;">&nbsp;(Mr), developer</span><br>Junjie Zhou (Mr), developer<br>Toni Anatalo (Mr), technical advisor<br></div><div style="line-height: 18.5714px; clear: both;"></div><p style="line-height: 18.5714px;"></p><p><b style="line-height: 18.5714px;">From&nbsp;<span style="line-height: 18.5714px;">Urban Planning division, Oulu city:</span></b><br style="line-height: 18.5714px;"></p><div style="line-height: 18.5714px; float: left;"><img src="data:image/jpeg;base64," data-filename="city.jpg" style="width: 176.271px; height: 26px;"></div><div style="line-height: 18.5714px; float: left; margin-left: 5px;">Jere Klami (Mr.)<br>Timo Mukari (Mr.)&nbsp;</div><div style="line-height: 18.5714px; clear: both;"></div><p><span style="line-height: 18.5714px;">To contact us, email&nbsp;</span><b style="line-height: 18.5714px;">df.thangld